import React from "react";
import { Container, Row, Col } from "reactstrap";

import me_pic from "../../images/me.jpg";
import "./About.css";
import Card from "../Card/Card";

const about = () => {
  console.log("Here in about");
  return (
    <div className="About">
      <div className="aboutContainer">
        <Container className="align-items-center">
          <Row>
            <Col className="col-md-10">
              <h2>Robert Selkirk Knox</h2>
              <img
                src={me_pic}
                alt="me"
                style={{ height: "350px", width: "250px" }}
              ></img>
              <p style={{ width: "100%", margin: "auto", paddingTop: "1em" }}>
                Robert Selkirk Knox is a writer of crime fiction. After
                finishing school at UCSC, he lived in Barcelona, Rio de janeiro,
                France and San Francisco. He has written two novels. He has a
                vague idea of traveling the world, setting a crime novel in each
                place he visits.
              </p>
            </Col>
          </Row>
        </Container>
      </div>
    </div>
  );
};

export default about;
